package Pattern;

public class Pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int row=5;
        int col=1;
        for(int i=0;i<row;i++) {
     	   for(int j=0;j<col;j++)
     	   {
     		   System.out.print(i+1);
     	   }
     	   col++;
     	   System.out.println();
        }
	}

}

